import java.awt.*;import javax.swing.*;import java.awt.event.*;class studendAdmissionForm extends JPanel implements ActionListener{	JTextField txtFirstName,txtFatherName,txtSurName;	JTextField txtAdmissionNo,txtAdmissionDate; JComboBox<String> Standards,Division;	JTextField txtPOB,txtDOB;JComboBox<String> national,motherLanguage;	JTextField txtMotherName,txtAdherNo;JComboBox<String> JGender,JCast;	JTextField txtFeesPaid;JComboBox<String> AdmQuta,AdmType;	JLabel studentImage;JButton upload;	JButton AdmitStudent;	public studendAdmissionForm(){		JPanel leftPanel = new JPanel();		leftPanel.setPreferredSize(new Dimension(410,400));		JPanel rightPanel = new JPanel();		rightPanel.setPreferredSize(new Dimension(75,142));		leftPanel.add(createTitlePanel());		leftPanel.add(createAdmissionDetailPanel());		leftPanel.add(createFullNamePanel());		leftPanel.add(createStudentDetailPanel_1());		leftPanel.add(createStudentDetailPanel());		leftPanel.add(createFeesDetailPanel());		leftPanel.add(createButtonPanel());				rightPanel.add(ImagePanel());		add(leftPanel,BorderLayout.EAST);		add(rightPanel,BorderLayout.WEST + BorderLayout.NORTH);		setPreferredSize(new Dimension(550,400));		setBackground(new Color(130,140,120));	}	public JPanel createTitlePanel(){		JPanel temp = new JPanel();		JLabel title = new JLabel("Student Admission Form");		title.setFont(new Font("consolas",Font.BOLD,25));		title.setForeground(Color.red);		temp.setPreferredSize(new Dimension(400,50));		temp.add(title);		temp.setBackground(new Color(200,240,220));		return temp;	}	public JPanel createFullNamePanel(){		JPanel temp = new JPanel();		temp.setLayout(new GridLayout(2,3));		temp.setPreferredSize(new Dimension(400,50));		JLabel firstName,fatherName,LastName;		firstName = new JLabel("Name");		fatherName = new JLabel("Father's Name");		LastName = new JLabel("Surname");		txtFirstName = new JTextField(20);		txtFatherName = new JTextField(20);		txtSurName = new JTextField(20);		temp.add(LastName);temp.add(firstName);temp.add(fatherName);		temp.add(txtSurName);temp.add(txtFirstName);temp.add(txtFatherName);		return temp;	}		public JPanel createAdmissionDetailPanel(){		JPanel temp = new JPanel();		temp.setLayout(new GridLayout(2,3));		temp.setPreferredSize(new Dimension(400,50));		JLabel admissionNo,standard,admDate,division;		admissionNo = new JLabel("Admission No");		standard = new JLabel("Standard Admitted");		division = new JLabel("Division");		admDate = new JLabel("Admission Date");		String arr[] = {"I","II","III","IV","V","VI","VII","VIII","IX","X","XI","XII"};	    String divarr[] = {"A","B"};		txtAdmissionDate= new JTextField(20);		txtAdmissionNo = new JTextField(20);		Standards = new JComboBox<String>(arr);		Division = new JComboBox<String>(divarr);		temp.add(admissionNo);temp.add(standard);temp.add(division);temp.add(admDate);		temp.add(txtAdmissionNo);temp.add(Standards);temp.add(Division);temp.add(txtAdmissionDate);		return temp;			}		public JPanel createStudentDetailPanel(){		JPanel temp = new JPanel();		temp.setLayout(new GridLayout(2,3));		temp.setPreferredSize(new Dimension(400,50));		JLabel placeDOB,motherTonge,nationality,DOB;		placeDOB = new JLabel("Place of Birth");		motherTonge = new JLabel("Mother Tongue");		nationality = new JLabel("Nationality");		DOB = new JLabel("Date of Birth");		String LangArr[] = {"Marathi","Hindi","English","Other"};	    String nationalities[] = {"Indian","Non-Indian"};		txtDOB= new JTextField(20);		motherLanguage = new JComboBox<String>(LangArr);		national = new JComboBox<String>(nationalities);		txtPOB = new JTextField(20);		temp.add(placeDOB);temp.add(motherTonge);temp.add(nationality);temp.add(DOB);		temp.add(txtPOB);temp.add(motherLanguage);temp.add(national);temp.add(txtDOB);		return temp;	}		public JPanel createStudentDetailPanel_1(){		JPanel temp = new JPanel();		temp.setLayout(new GridLayout(2,3));		temp.setPreferredSize(new Dimension(400,50));		JLabel adharNo,motherName,gender,cast;		motherName = new JLabel("Mother's Name");		adharNo = new JLabel("Adhar No");		gender = new JLabel("Gender");		cast = new JLabel("Cast");		String CastArr[] = {"Open","OBC","SBC","SC","NT","ST"};	    String GenederArr[] = {"Male","Female","Other"};		txtMotherName= new JTextField(15);		JCast = new JComboBox<String>(CastArr);		JGender = new JComboBox<String>(GenederArr);		txtAdherNo = new JTextField(20);		temp.add(motherName);temp.add(adharNo);temp.add(gender);temp.add(cast);		temp.add(txtMotherName);temp.add(txtAdherNo);temp.add(JGender);temp.add(JCast);		return temp;	}		public JPanel createFeesDetailPanel(){		JPanel temp = new JPanel();		temp.setLayout(new GridLayout(2,3));		temp.setPreferredSize(new Dimension(400,50));		JLabel admisionType,admissionQuta,FeesPaid;		admisionType = new JLabel("Admission Type");		String CastArr[] = {"Open","OBC","SBC","SC","NT","ST"};		AdmType = new JComboBox<String>(CastArr);		admissionQuta = new JLabel("Admission Quta");		String Quta[] = {"Government","Non-Government","Semi-Government"};		AdmQuta = new JComboBox<String>(Quta);		FeesPaid = new JLabel("Fees Paid");		txtFeesPaid = new JTextField(15);		temp.add(admisionType);temp.add(admissionQuta);temp.add(FeesPaid);		temp.add(AdmType);temp.add(AdmQuta);temp.add(txtFeesPaid);		return temp;	}	public JPanel ImagePanel(){		JPanel temp = new JPanel();		temp.setPreferredSize(new Dimension(70,140));		JPanel imagePane = new JPanel();		imagePane.setPreferredSize(new Dimension(70,100));		studentImage = new JLabel("No Image");		imagePane.setBackground(Color.white);		upload = new JButton("upload");		upload.addActionListener(this);		imagePane.add(studentImage);		temp.add(imagePane);		temp.add(upload);		return temp;	}		public JPanel createButtonPanel(){		JPanel temp = new JPanel();		temp.setLayout(new FlowLayout());		temp.setPreferredSize(new Dimension(400,50));		AdmitStudent = new JButton("Admit Student");		AdmitStudent.setFont(new Font("David",Font.BOLD,16));		AdmitStudent.setBackground(Color.black);		AdmitStudent.setForeground(Color.white);		temp.add(AdmitStudent,BorderLayout.CENTER);		return temp;	}	public static void main(String args[]){		new frameTemplate(new studendAdmissionForm());	}		public void actionPerformed(ActionEvent ae){		try{			if(ae.getSource() == upload){				ImageIcon icoImg = UploadImageClass.UploadImage(70,100);				studentImage.setIcon(icoImg);				studentImage.setPreferredSize(new Dimension(70,100));			}		}catch(Exception e){					}	}	}